const printResult1 = (result: Either<string, number>) => {
  fold(
    result,
    (error: string) => console.log(`Error: ${error}`),
    (value: number) => console.log(`Result: ${value}`)
  );
};

// Method 2: Using the class method with type assertion
const printResult2 = (result: Either<string, number>) => {
