		if (rows * columns !== this.rows * this.cols) {
			throw new Error("New matrix size must match existing matrix size.");
		}

		const result = new Matrix(rows, columns, this.data);

		return result;
	}

	// Scalar multiplication
	scalarMultiply(scalar: number): Matrix {
		const result = new Matrix(this.rows, this.cols);
		for (let i = 0; i < this.data.length; i++) {
			result.data[i] = this.data[i] * scalar;
		}
		return result;
	}

	// Element-wise multiplication (Hadamard product)
	elementWiseMultiply(other: Matrix): Matrix {
		if (this.rows !== other.rows || this.cols !== other.cols) {
			throw new Error(
				"Matrices must have same dimensions for element-wise multiplication",
			);
		}

		const result = new Matrix(this.rows, this.cols);
		for (let i = 0; i < this.data.length; i++) {
			result.data[i] = this.data[i] * other.data[i];
		}
		return result;
	}

	mean(mode: "row" | "column"): number[] {
		if (mode === "row") {
			const results: number[] = [];
			for (let i = 0; i < this.rows; i++) {
				let mean = 0;
				for (let j = 0; j < this.cols; j++) {
					mean += this.data[i * this.cols + j];
				}
				results.push(mean / this.cols);
			}
			return results;
		}

		const results: number[] = [];
		for (let i = 0; i < this.cols; i++) {
			let mean = 0;
			for (let j = 0; j < this.rows; j++) {
				mean += this.data[j * this.cols + i];
			}
			results.push(mean / this.rows);
		}
		return results;
	}

