				let v = new Array(n);
				for (let i = 0; i < n; i++) {
					v[i] = matrix[i * n + j];
				}

				for (let k = 0; k < j; k++) {
					let dot = 0;
					for (let i = 0; i < n; i++) {
						dot += v[i] * q[i * n + k];
					}
					for (let i = 0; i < n; i++) {
						v[i] -= dot * q[i * n + k];
					}
				}

				let norm = Math.sqrt(v.reduce((sum, x) => sum + x * x, 0));
				if (norm < tolerance) norm = tolerance;

				for (let i = 0; i < n; i++) {
					q[i * n + j] = v[i] / norm;
				}
			}

			// Compute R = Q^T * A
			for (let i = 0; i < n; i++) {
				for (let j = 0; j < n; j++) {
					let sum = 0;
					for (let k = 0; k < n; k++) {
						sum += q[k * n + i] * matrix[k * n + j];
					}
					r[i * n + j] = sum;
				}
			}

			// New iteration matrix = R * Q
			let newMatrix = new Array(n * n);
			for (let i = 0; i < n; i++) {
				for (let j = 0; j < n; j++) {
					let sum = 0;
					for (let k = 0; k < n; k++) {
						sum += r[i * n + k] * q[j * n + k];
					}
					newMatrix[i * n + j] = sum;
				}
			}

			// Check convergence
			let converged = true;
			for (let i = 0; i < n * n; i++) {
				if (Math.abs(newMatrix[i] - matrix[i]) > tolerance) {
					converged = false;
					break;
				}
			}

			matrix = newMatrix;
			if (converged) break;
		}

		// Extract diagonal elements (eigenvalues)
		let eigenvalues = [];
		for (let i = 0; i < n; i++) {
			eigenvalues.push(matrix[i * n + i]);
		}

		return eigenvalues;
	}

	// Create identity matrix
	static identity(size: number): Matrix {
		const result = new Matrix(size, size);
		for (let i = 0; i < size; i++) {
			result.set(i, i, 1);
		}
		return result;
	}

	// Helper method to validate indices
	private validateIndices(i: number, j: number): void {
		if (i < 0 || i >= this.rows || j < 0 || j >= this.cols) {
			throw new Error("Index out of bounds");
		}
	}

	// Get matrix dimensions
	get shape(): [number, number] {
		return [this.rows, this.cols];
	}

	// Get raw data
	get rawData(): number[] {
		return [...this.data];
	}

	// Pretty print matrix
	toString(): string {
		let result = "";
		for (let i = 0; i < this.rows; i++) {
			for (let j = 0; j < this.cols; j++) {
				result += this.get(i, j) + " ";
			}
			result += "\n";
		}
		return result;
	}
}
