        }

        let det = 0;
        for (let j = 0; j < this.cols; j++) {
            det += Math.pow(-1, j) * this.get(0, j) * this.minor(0, j).determinant();
        }
        return det;
    }

    // Get minor matrix (remove row i and column j)
    minor(row: number, col: number): Matrix {
        const result = new Matrix(this.rows - 1, this.cols - 1);
        let r = 0, c = 0;

        for (let i = 0; i < this.rows; i++) {
            if (i === row) continue;
            c = 0;
            for (let j = 0; j < this.cols; j++) {
                if (j === col) continue;
                result.set(r, c, this.get(i, j));
                c++;
            }
            r++;
        }
        return result;
    }

    // Calculate adjugate matrix
    adjugate(): Matrix {
        if (this.rows !== this.cols) {
            throw new Error("Matrix must be square to calculate adjugate");
        }

        const result = new Matrix(this.rows, this.cols);
        for (let i = 0; i < this.rows; i++) {
            for (let j = 0; j < this.cols; j++) {
                const cofactor = Math.pow(-1, i + j) * this.minor(i, j).determinant();
                result.set(j, i, cofactor); // Note: transpose while building
            }
        }
        return result;
    }

    // Calculate inverse matrix
    inverse(): Matrix {
        const det = this.determinant();
        if (Math.abs(det) < 1e-10) {
