        throw this.value;
    }
    return this.value as T;
}

failSilently(callback: (e: Error) => void) {
    if (this.isFailure()) {
        callback(this.value as Error);
        return Option.none<T>();
    }
    return Option.some<T>(this.value as T);
}
```

