    let processFn: (ptr: number, length: number) => void;

    if (numbers instanceof Float32Array) {
      typedArray = numbers;
      processFn = this.wasmModule.processFloat32;
    } else if (numbers instanceof Float64Array) {
      typedArray = numbers;
      processFn = this.wasmModule.processFloat64;
