import {
  type EmbedManyResult,
  type EmbeddingModel,
  type LanguageModel,
  embedMany,
  generateText,
} from "ai";

interface Question {
  text: string;
  embedding: number[];
}

interface Demonstration {
  question: Question;
  rationale: string;
}

interface Cluster {
  centroid: number[];
  points: Question[];
}

class ECHOImplementation {
  private model: LanguageModel;
  private embeddingModel: EmbeddingModel<string>;

  constructor(model: LanguageModel, embeddingModel: EmbeddingModel<string>) {
    this.model = model;
    this.embeddingModel = embeddingModel;
  }

  // Step 1: Question Clustering
  async clusterQuestions(
    questions: string[],
    numClusters: number,
  ): Promise<Cluster[]> {
    // Generate embeddings for all questions
    const result = await embedMany({
      model: this.embeddingModel,
      values: questions,
    });

    // Implement k-means clustering
    return this.kMeansClustering(result, numClusters);
  }

  // Step 2: Demonstration Sampling
  async sampleDemonstrations(clusters: Cluster[]): Promise<Demonstration[]> {
    const demonstrations: Demonstration[] = [];

    for (const cluster of clusters) {
      // Select representative question (e.g., closest to centroid)
      const representative = this.selectRepresentative(cluster);

      // Generate initial rationale using zero-shot CoT
      const rationale = await this.generateZeroShotCoT(representative.text);

      demonstrations.push({
        question: representative,
        rationale,
      });
    }

    return demonstrations;
  }

  // Step 3: Demonstration Unification
  async unifyDemonstrations(
    demonstrations: Demonstration[],
    numIterations: number,
  ): Promise<Demonstration[]> {
    const currentDemonstrations = [...demonstrations];

    for (let i = 0; i < numIterations; i++) {
      // Randomly select a demonstration to regenerate
      const indexToRegenerate = Math.floor(
        Math.random() * demonstrations.length,
      );

      // Create context from other demonstrations
      const context = this.createContext(
        currentDemonstrations.filter((_, index) => index !== indexToRegenerate),
      );

      // Generate new rationale using context
      const newRationale = await this.generateWithContext(
        currentDemonstrations[indexToRegenerate].question.text,
        context,
      );

      // Update the demonstration
      currentDemonstrations[indexToRegenerate] = {
        ...currentDemonstrations[indexToRegenerate],
        rationale: newRationale,
      };
    }

    return currentDemonstrations;
  }

  // Helper methods
  private async generateZeroShotCoT(question: string): Promise<string> {
    const prompt = `Let's solve this step by step:\nQuestion: ${question}\nLet's approach this step by step:`;
    const { text } = await generateText({
      model: this.model,
      prompt,
    });
    return text;
  }

  private createContext(demonstrations: Demonstration[]): string {
    return demonstrations
      .map((d) => `Question: ${d.question.text}\nSolution: ${d.rationale}`)
      .join("\n\n");
  }

  private async generateWithContext(
    question: string,
    context: string,
  ): Promise<string> {
    const prompt = `Here are some examples of solving similar problems:\n\n${context}\n\nNow, let's solve this question:\n${question}\nLet's approach this step by step:`;
    const { text } = await generateText({
      model: this.model,
      prompt,
    });
    return text;
  }

  private kMeansClustering(
    questions: EmbedManyResult<string>,
    k: number,
  ): Cluster[] {
    const points: Question[] = zip(questions.values, questions.embeddings).map(
      (q) => ({
        embedding: q[1],
        text: q[0],
      }),
    );

    // Initialize k random centroids
    let centroids = Array.from({ length: k }, () => {
      const randomIndex = Math.floor(Math.random() * points.length);
      return [...points[randomIndex].embedding];
    });

    let clusters: Cluster[];
    let previousClusters: Cluster[] | null = null;
    const maxIterations = 100;
    let iteration = 0;
    do {
      // Assign points to nearest centroid
      clusters = centroids.map((centroid) => ({
        centroid,
        points: [],
      }));

      for (const point of points) {
        let minDistance = Number.POSITIVE_INFINITY;
        let closestClusterIndex = 0;

        centroids.forEach((centroid, index) => {
          const distance = this.euclideanDistance(point.embedding, centroid);
          if (distance < minDistance) {
            minDistance = distance;
            closestClusterIndex = index;
          }
        });

        clusters[closestClusterIndex].points.push(point);
      }

      // Update centroids
      centroids = clusters.map((cluster) => {
        if (cluster.points.length === 0) return cluster.centroid;

        const newCentroid = new Array(cluster.points[0].embedding.length).fill(
          0,
        );
        for (const point of cluster.points) {
          point.embedding.forEach((value, dim) => {
            newCentroid[dim] += value;
          });
        }

        return newCentroid.map((sum) => sum / cluster.points.length);
      });

      // Check convergence
      const hasConverged =
        previousClusters && this.clustersEqual(clusters, previousClusters);

      if (hasConverged || iteration >= maxIterations) break;

      previousClusters = JSON.parse(JSON.stringify(clusters));
      iteration++;
    } while (true);

    return clusters;
  }

  private euclideanDistance(a: number[], b: number[]): number {
    return Math.sqrt(a.reduce((sum, value, i) => sum + (value - b[i]) ** 2, 0));
  }

  private clustersEqual(c1: Cluster[], c2: Cluster[]): boolean {
    return c1.every((cluster, i) =>
      cluster.centroid.every(
        (value, j) => Math.abs(value - c2[i].centroid[j]) < 0.0001,
      ),
    );
  }

  private selectRepresentative(cluster: Cluster): Question {
    let minDistance = Number.MAX_VALUE;
    let representative: Question | null = null;

    for (const point of cluster.points) {
      const distance = this.euclideanDistance(
        point.embedding,
        cluster.centroid,
      );
      if (distance < minDistance) {
        minDistance = distance;
        representative = point;
      }
    }

    if (!representative) {
      throw new Error("Cluster has no points");
    }

    return representative;
  }
}

function zip<T, U>(arr1: T[], arr2: U[]): [T, U][] {
  const length = Math.min(arr1.length, arr2.length);
  const result: [T, U][] = [];

  for (let i = 0; i < length; i++) {
    result.push([arr1[i], arr2[i]]);
  }

  return result;
}

// Usage example
async function main() {
  const echo = new ECHOImplementation(llm, embeddingModel);

  // Sample questions
  const questions = [
    "What is 25% of 80?",
    "If a train travels 120 km in 2 hours, what is its speed?",
    // ... more questions
  ];

  // Execute ECHO process
  const clusters = await echo.clusterQuestions(questions, 3);
  const initialDemonstrations = await echo.sampleDemonstrations(clusters);
  const finalDemonstrations = await echo.unifyDemonstrations(
    initialDemonstrations,
    5,
  );

  console.log("Final demonstrations:", finalDemonstrations);
}

```
</implementation>

