      const hasConverged =
        previousClusters && this.clustersEqual(clusters, previousClusters);

      if (hasConverged || iteration >= maxIterations) break;

      previousClusters = JSON.parse(JSON.stringify(clusters));
      iteration++;
    } while (true);

    return clusters;
  }

  private async satisfiesSelectionCriteria(
    question: string,
    rationale: string,
  ): Promise<boolean> {
    // Implement selection criteria
    // This could include:
    // - Length checks
    // - Quality metrics
    // - Structure validation
    // - etc.
  }

  private sortClusterPoints(cluster: Cluster): Question[] {
    return [...cluster.points].sort(
      (a, b) =>
        this.euclideanDistance(a.embedding, cluster.centroid) -
        this.euclideanDistance(b.embedding, cluster.centroid),
    );
  }

  private euclideanDistance(a: number[], b: number[]): number {
    return Math.sqrt(a.reduce((sum, value, i) => sum + (value - b[i]) ** 2, 0));
  }

  private shuffleArray<T>(array: T[]): T[] {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  }

  private clustersEqual(c1: Cluster[], c2: Cluster[]): boolean {
    return c1.every((cluster, i) =>
      cluster.centroid.every(
        (value, j) => Math.abs(value - c2[i].centroid[j]) < 0.0001,
      ),
    );
  }
}

function zip<T, U>(arr1: T[], arr2: U[]): [T, U][] {
  const length = Math.min(arr1.length, arr2.length);
  const result: [T, U][] = [];

  for (let i = 0; i < length; i++) {
    result.push([arr1[i], arr2[i]]);
  }

  return result;
}

