               "Provides detailed error handling and supports various encodings. " +
               "Can read single or multiple files in one operation.",
  parameters: z.object({
    paths: z.union([
      z.string(),
      z.array(z.string())
    ]).describe("Single file path or array of file paths to read"),
    options: z.object({
      isImage: z.boolean().default(false)
        .describe("Set to true for image files to get base64 encoded result"),
      encoding: fileEncodingSchema.default("utf-8")
        .describe("Encoding format for reading text files"),
      batch: z.boolean().default(false)
        .describe("Set to true when reading multiple files")
    }).optional().default({})
  }),
  execute: async ({ paths, options = {} }) => {
    const { isImage, encoding, batch } = options;

    // Handle single file case
    if (typeof paths === "string") {
      const validPath = await validatePath(paths, allowedDirectories);

      if (isImage) {
        const file = await fs.readFile(validPath);
        return `data:image/${path.extname(validPath).toLowerCase().replace(".", "")};base64,${Buffer.from(file).toString("base64")}`;
      }

      return await fs.readFile(validPath, encoding);
    }

    // Handle multiple files case
    if (batch && Array.isArray(paths)) {
      const results = await Promise.all(
        paths.map(async (filePath) => {
          try {
            const validPath = await validatePath(filePath, allowedDirectories);
            const content = await fs.readFile(validPath, encoding);
            return { path: filePath, content, success: true };
          } catch (error) {
            return {
              path: filePath,
              error: error instanceof Error ? error.message : String(error),
              success: false
            };
          }
        })
      );

      return results;
    }

    throw new Error("Invalid parameters: paths must be a string or array when batch is true");
  }
});
