```typescript
const consolidatedCreateDirectory = tool({
  description: "Create new directories with support for nested paths. " +
               "Ensures directories exist and handles recursive creation.",
  parameters: z.object({
    path: z.string().describe("Path to directory to create"),
    options: z.object({
      recursive: z.boolean().default(true)
        .describe("Create parent directories as needed"),
      mode: z.number().optional()
        .describe("Optional permissions for the directory"),
      failIfExists: z.boolean().default(false)
        .describe("Throw error if directory already exists")
    }).optional().default({})
  }),
  execute: async ({ path: dirPath, options = {} }) => {
    const validPath = await validatePath(dirPath, allowedDirectories);

    try {
      const exists = await fs.access(validPath)
        .then(() => true)
        .catch(() => false);

      if (exists && options.failIfExists) {
        throw new Error(`Directory already exists: ${dirPath}`);
      }

      await fs.mkdir(validPath, {
        recursive: options.recursive,
        mode: options.mode
      });

      return {
        success: true,
        path: validPath,
        message: exists ? "Directory already exists" : "Directory created successfully"
      };
    } catch (error) {
      throw new Error(`Failed to create directory: ${error instanceof Error ? error.message : String(error)}`);
    }
  }
});
