import fs from "node:fs";
import { tool } from "ai";
import path from "node:path";
import simpleGit from "simple-git";
import { z } from "zod";
const CONVENTIONAL_COMMIT_MESSAGE = /^(feat|fix|docs|style|refactor|perf|test|chore)(\(\w+\))?!?: .+/;
function validateConventionalCommit(message) {
    return CONVENTIONAL_COMMIT_MESSAGE.test(message);
}
const sanitizePath = (workingDir, userPath) => {
    const resolvedPath = path.resolve(workingDir, userPath);
    if (!resolvedPath.startsWith(workingDir)) {
        throw new Error("Path is outside the working directory.");
    }
    const gitDir = path.join(resolvedPath, ".git");
    try {
        const stats = fs.statSync(gitDir);
        if (!stats.isDirectory()) {
            throw new Error("Not a git repository");
        }
    }
    catch (_error) {
        throw new Error("Not a git repository");
    }
    return resolvedPath;
};
export const createGitTools = async ({ workingDir }) => {
    return {
        gitCommit: tool({
            description: "Commits a new git changeset for the given files with the provided commit message. It will stage the files given if they aren't already staged. The commit message should adhere to the Conventional Commits standard.",
            parameters: z.object({
                path: z.string(),
                message: z.string().describe("The commit message."),
                files: z
                    .string()
                    .describe("A command-separated list of files to include in this commit."),
            }),
            execute: async ({ path, message, files }) => {
                try {
                    const baseDir = sanitizePath(workingDir, path);
                    const git = simpleGit({ baseDir });
                    // Check if there are any changes to commit
                    const status = await git.status();
                    if (status.files.length === 0) {
                        return "No changes to commit.";
                    }
                    // Check if no message is provided or the provided message doesn't conform to Conventional Commits
                    if (!(message && validateConventionalCommit(message))) {
                        return "Invalid commit message. Doesn't conform to Conventional Commits";
                    }
                    if (!files || files.trim() === "") {
                        return "No files provided.";
                    }
                    const fileArr = files
                        .split(",")
                        .map((file) => file.trim())
                        .map((file) => sanitizePath(workingDir, file));
                    // Add the changes and commit
                    await git.add(fileArr);
                    const commitResult = await git.commit(message);
                    return `Commit created successfully: ${commitResult.commit} - ${message}`;
                }
                catch (error) {
                    return `Error creating commit: ${error.message}`;
                }
            },
        }),
        gitStatus: tool({
            description: "Get the status of the git repo at the given path.",
            parameters: z.object({
                path: z.string(),
            }),
            execute: async ({ path }) => {
                try {
                    const baseDir = sanitizePath(workingDir, path);
                    const git = simpleGit({ baseDir });
                    // Check if there are any changes to commit
                    const status = await git.status();
                    if (status.files.length === 0) {
                        return "No changes found.";
                    }
                    status;
                    return `Status:\n ${JSON.stringify(status, undefined, 2)}`;
                }
                catch (error) {
                    return `Error getting status: ${error.message}`;
                }
            },
        }),
        gitDiff: tool({
            description: "Shows differences between branches or commits",
            parameters: z.object({
                path: z.string(),
                target: z.string(),
            }),
            execute: async ({ path, target }) => {
                try {
