    });
    rtoCompletionTokens += q2Response.length;
    // Generate second code based on the description (C2)
    const { text: c2Response } = await generateText({
        model,
        maxTokens: 4096,
        temperature: 0.1,
        system: system,
        prompt: q2Response,
