    const { text: c3Response } = await generateText({
        model,
        maxTokens: 4096,
        temperature: 0.1,
        system: prompt,
        prompt: finalPrompt,
    });
    rtoCompletionTokens += c3Response.length;
    return [c3Response, rtoCompletionTokens];
};

