import { generateObject, generateText } from "ai";
import { z } from "zod";
class ThoughtNode {
    content;
    children;
    score;
    constructor(content, score = 0) {
        this.content = content;
        this.children = [];
        this.score = score;
    }
    addChild(child) {
        this.children.push(child);
    }
}
class TreeOfThought {
    model;
    system;
    root;
    maxDepth;
    branchingFactor;
    constructor({ model, system, initialThought, maxDepth = 3, branchingFactor = 3, }) {
        this.model = model;
        this.system = system;
        this.root = new ThoughtNode(initialThought);
        this.maxDepth = maxDepth;
        this.branchingFactor = branchingFactor;
    }
    async generateThought(parentThought) {
        const { text } = await generateText({
            model: this.model,
            temperature: 0.7,
            maxTokens: 200,
            system: this.system,
            prompt: `Next thought based on: ${parentThought}`,
        });
        return text;
    }
    async evaluateThought(thought) {
        const { object } = await generateObject({
            model: this.model,
            temperature: 0.7,
            maxTokens: 200,
            schema: z.object({ score: z.number() }),
            prompt: `Evaluate: ${thought}`,
        });
        return object.score;
    }
    async expand(node, depth) {
        if (depth >= this.maxDepth)
            return;
        for (let i = 0; i < this.branchingFactor; i++) {
