          content: finalMessage,
        });
      }

      const fsTools = await tools.createFileSystemTools({
        workingDir: projectDir ?? `${baseDir}/temp`,
      });

      const gitTools = await tools.createGitTools({
        workingDir: projectDir ?? `${baseDir}/temp`,
      });

      const codeInterpreterTool = tools.createCodeInterpreterTool({});

      const raindropTools = tools.createRaindropTools({
        apiKey: process.env["RAINDROP_API_KEY"] ?? "",
      });

      const urlTools = tools.createUrlTools();

      const allTools = {
        ...fsTools,
        ...gitTools,
        ...codeInterpreterTool,
        ...raindropTools,
        ...urlTools,
      };

      const { text } = await generateText({
        model: wrapLanguageModel(
          languageModel((model as ModelName) ?? "anthropic:sonnet"),
          middleware.log,
          middleware.usage,
          middleware.auditMessage,
        ),
        temperature: temperature ?? 0.3,
        maxTokens: maxTokens ?? 8192,
        tools: allTools,
        experimental_activeTools: ["searchBookmarks", "readUrl"],
        system: `You are a very helpful assistant that is focused on helping solve hard problems.`,
        messages,
        maxSteps: 5,
      });

      const result = `${message}\n\n${text}`;

      return c.json(
        {
          content: result.trim(),
        },
        200,
      );
    },
  );

