    zValidator(
      "query",
      z.object({
        model: z.string().optional(),
        maxTokens: z.number().optional(),
        temperature: z.number().optional(),
      }),
    ),
    async (c) => {
      const { model, maxTokens, temperature } = c.req.valid("query");
      const result = "Hello, Travis!";
      messages.length = 0;
      return c.html(
        <ChatTop
          text={result}
          messages={messages}
          selectedModel={(model ?? "anthropic:sonnet") as ModelName}
          maxTokens={maxTokens ?? 8192}
          temperature={temperature ?? 0.3}
        />,
      );
    },
  )
  .post(
    "/",
    zValidator(
      "form",
      z.object({
        model: z.string().optional(),
        maxTokens: z.coerce.number().optional(),
        temperature: z.coerce.number().optional(),
        message: z.string(),
        mode: z.string().optional(),
      }),
    ),
    async (c) => {
      const { model, maxTokens, temperature, message, mode } =
        c.req.valid("form");

      messages.push({
        role: "user",
        content: message,
      });

      const { text } = await generateText({
        model: wrapLanguageModel(
          languageModel((model as ModelName) ?? "anthropic:sonnet"),
          middleware.log,
          middleware.usage,
          middleware.auditMessage,
        ),
        temperature: temperature ?? 0.3,
        maxTokens: maxTokens ?? 8192,
        system: `You are a very helpful assistant that is focused on helping solve hard problems.`,
        messages: messages,
      });

      messages.push({
        role: "assistant",
        content: text,
      });

      return c.html(
        <ChatTop
          text={""}
          messages={messages}
          selectedModel={(model ?? "anthropic:sonnet") as ModelName}
          maxTokens={maxTokens ?? 8192}
          temperature={temperature ?? 0.3}
        />,
      );
    },
  );

export default app;

