    );
    return selectedChild;
  }

  async simulate(node: MCTSNode): Promise<number> {
    console.log(
      `Starting simulation from node. Current query: ${node.state.currentQuery}`,
    );
    let state = node.state;

    for (let i = 0; i < this.simulationDepth; i++) {
