      const prevUcb1 =
        prev.value / (prev.visits + 1e-8) +
        this.explorationWeight *
          Math.sqrt(Math.log(node.visits + 1) / (prev.visits + 1e-8));

      return ucb1 > prevUcb1 ? current : prev;
